import{_ as s,o as i,c as a,R as n}from"./chunks/framework.ymTWbDx7.js";const c=JSON.parse('{"title":"一篇搞定面试中的跨域问题","description":"跨源资源共享（CORS，或通俗地译为跨域资源共享）是一种基于 HTTP 头的机制，该机制通过允许服务器标示除了它自己以外的其他源（域、协议或端口），使得浏览器允许这些源访问加载自己的资源。跨源资源共享还通过一种机制来检查服务器是否会允许要发送的真实请求，该机制通过浏览器发起一个到服务器托管的跨源资源的“预检”请求。在预检中，浏览器发送的头中标示有 HTTP 方法和真实请求中会用到的头。。","frontmatter":{"title":"一篇搞定面试中的跨域问题","head":[["meta",{"name":"description","content":"跨源资源共享（CORS，或通俗地译为跨域资源共享）是一种基于 HTTP 头的机制，该机制通过允许服务器标示除了它自己以外的其他源（域、协议或端口），使得浏览器允许这些源访问加载自己的资源。跨源资源共享还通过一种机制来检查服务器是否会允许要发送的真实请求，该机制通过浏览器发起一个到服务器托管的跨源资源的“预检”请求。在预检中，浏览器发送的头中标示有 HTTP 方法和真实请求中会用到的头。。"}],["meta",{"name":"keywords","content":"cors,proxy"}]],"layout":"doc"},"headers":[],"relativePath":"interview/cors.md","filePath":"interview/cors.md","lastUpdated":1708055483000}'),t={name:"interview/cors.md"},h=n("",48),e=[h];function p(l,k,r,E,d,o){return i(),a("div",null,e)}const y=s(t,[["render",p]]);export{c as __pageData,y as default};
